import React, { useState } from "react";
import Card from "./Card";
import SearchBar from "./SearchBar";
import { FormControlLabel, Checkbox } from "@mui/material";
import cards from "./cardsData";

export default function CardList() {
  const [filteredCards, setFilteredCards] = useState(cards);
  const [hideOutOfStock, setHideOutOfStock] = useState(false);

  const handleSearch = (searchTerm, sortBy) => {
    const sortedCards = cards
      .slice()
      .sort((a, b) =>
        sortBy === "price"
          ? a.price - b.price
          : sortBy === "price-reverse"
          ? b.price - a.price
          : b.inStock - a.inStock
      )
      .filter((card) =>
        `${card.name} ${card.price}`.toLowerCase().includes(searchTerm.toLowerCase())
      );

    setFilteredCards(hideOutOfStock ? sortedCards.filter((card) => card.inStock) : sortedCards);
  };

  const handleHideOutOfStockChange = (event) => {
    setHideOutOfStock(event.target.checked);
    setFilteredCards(event.target.checked ? filteredCards.filter((card) => card.inStock) : cards);
  };

  const renderCards = () => {
    return (
      <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))", gap: "20px" }}>
        {filteredCards.map((card) => (
          <Card key={card.name} {...card} style={{ display: "inline-block" }} />
        ))}
      </div>
    );
  };

  return (
    <div>
      <SearchBar items={cards} onSearch={handleSearch} />
      <FormControlLabel control={<Checkbox checked={hideOutOfStock} onChange={handleHideOutOfStockChange} />} label="Hide out of stock" />
      {renderCards()}
    </div>
  );
}
