import React, { useState, useEffect } from "react";
import Card from "./Card";
import SearchBar from "./SearchBar";
import { FormControlLabel, Checkbox } from "@mui/material";
import cards from "../assetts/CardsData";

export default function CardList() {
  const [filteredCards, setFilteredCards] = useState(cards);
  const [hideOutOfStock, setHideOutOfStock] = useState(false);
  const [selectedBrand, setSelectedBrand] = useState("");

  useEffect(() => {
    setFilteredCards(
      hideOutOfStock
        ? cards.filter((card) => card.inStock && card.brand === selectedBrand)
        : cards.filter((card) => card.brand === selectedBrand)
    );
  }, [hideOutOfStock, selectedBrand]);

  const handleSearch = (searchTerm, sortBy) => {
    let sortedCards = [...cards];

    switch (sortBy) {
      case "price":
        sortedCards.sort((a, b) => a.price - b.price);
        break;
      case "price-reverse":
        sortedCards.sort((a, b) => b.price - a.price);
        break;
      case "availability":
        sortedCards.sort((a, b) => b.inStock - a.inStock);
        break;
      default:
        break;
    }

    const filteredCards = sortedCards.filter(
      (card) =>
        card.brand === selectedBrand &&
        `${card.name} ${card.price}`.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setFilteredCards(hideOutOfStock ? filteredCards.filter((card) => card.inStock) : filteredCards);
  };

  const handleBrandSelect = (event) => {
    setSelectedBrand(event.target.value);
  };

  const renderCards = () => {
    return filteredCards.map((card) => (
      <Card key={card.name} {...card} style={{ display: "inline-block" }}>
        <div>Brand: {card.brand}</div>
      </Card>
    ));
  };

  return (
    <div>
      <SearchBar items={cards} onSearch={handleSearch} />
      <FormControlLabel
        control={
          <Checkbox
            checked={hideOutOfStock}
            onChange={(e) => setHideOutOfStock(e.target.checked)}
          />
        }
        label="Hide out of stock"
      />
      <div>
        <label htmlFor="brand-select">Select a brand: </label>
        <select id="brand-select" value={selectedBrand} onChange={handleBrandSelect}>
          <option value="">All Brands</option>
          {Array.from(new Set(cards.map((card) => card.brand))).map((brand) => (
            <option key={brand} value={brand}>
              {brand}
            </option>
          ))}
        </select>
      </div>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
          gap: "20px",
        }}
      >
        {renderCards()}
      </div>
    </div>
  );
}
