import React, { useState, useEffect } from "react";
import Card from "./Card";
import SearchBar from "./SearchBar";
import { FormControlLabel, Checkbox, Select, MenuItem } from "@mui/material";
import cards from "../assetts/";

export default function CardList() {
  const [filteredCards, setFilteredCards] = useState(cards);
  const [hideOutOfStock, setHideOutOfStock] = useState(false);
  const [selectedBrand, setSelectedBrand] = useState("");

  useEffect(() => {
    const filteredByStock = hideOutOfStock ? cards.filter((card) => card.inStock) : cards;
    const filteredByBrand = selectedBrand ? filteredByStock.filter((card) => card.brand === selectedBrand) : filteredByStock;
    setFilteredCards(filteredByBrand);
  }, [hideOutOfStock, selectedBrand]);

  const handleSearch = (searchTerm, sortBy) => {
    let sortedCards = [...cards];

    switch (sortBy) {
      case "price":
        sortedCards.sort((a, b) => a.price - b.price);
        break;
      case "price-reverse":
        sortedCards.sort((a, b) => b.price - a.price);
        break;
      case "availability":
        sortedCards.sort((a, b) => b.inStock - a.inStock);
        break;
      default:
        break;
    }

    const filteredCards = sortedCards.filter((card) =>
      `${card.name} ${card.price} ${card.brand}`.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setFilteredCards(hideOutOfStock ? filteredCards.filter((card) => card.inStock) : filteredCards);
  };

  const handleBrandFilterChange = (event) => {
    setSelectedBrand(event.target.value);
  }

  const renderCards = () => {
    return filteredCards.map((card) => (
      <Card key={card.name} {...card} style={{ display: "inline-block" }}>
        <div>Brand: {card.brand}</div>
      </Card>
    ));
  };

  const brandList = [...new Set(cards.map((card) => card.brand))]; // Get unique list of brands

  return (
    <div>
      <SearchBar items={cards} onSearch={handleSearch} />
      <FormControlLabel
        control={
          <Checkbox
            checked={hideOutOfStock}
            onChange={(e) => setHideOutOfStock(e.target.checked)}
          />
        }
        label="Hide out of stock"
      />
      <div style={{ marginBottom: '20px' }}>
        <Select value={selectedBrand} onChange={handleBrandFilterChange}>
          <MenuItem value="">All Brands</MenuItem>
          {brandList.map((brand) => (
            <MenuItem key={brand} value={brand}>{brand}</MenuItem>
          ))}
        </Select>
      </div>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
          gap: "20px",
        }}
      >
        {renderCards()}
      </div>
    </div>
  );
}
