import React, { useState } from "react";

const SearchBar = ({ items, onSearch, onBrandChange }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("none");
  const [selectedBrand, setSelectedBrand] = useState("");

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
    onSearch(event.target.value, sortBy, selectedBrand);
  };

  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
    onSearch(searchTerm, event.target.value, selectedBrand);
  };

  const handleBrandChange = (event) => {
    setSelectedBrand(event.target.value);
    onBrandChange(event.target.value);
  };

  return (
    <form>
      <label htmlFor="searchTerm">Search:</label>
      <input
        type="text"
        id="searchTerm"
        value={searchTerm}
        onChange={handleSearchTermChange}
      />
      <label htmlFor="sortBy">Sort by:</label>
      <select id="sortBy" value={sortBy} onChange={handleSortByChange}>
        <option value="none">None</option>
        <option value="price">Price (Low to High)</option>
        <option value="price-reverse">Price (High to Low)</option>
        <option value="availability">Availability</option>
      </select>
      <label htmlFor="brand">Brand:</label>
      <select id="brand" value={selectedBrand} onChange={handleBrandChange}>
        <option value="">All</option>
        <option value="Magic: The Gathering">Magic: The Gathering</option>
        <option value="Yu-Gi-Oh!">Yu-Gi-Oh!</option>
        <option value="Pokémon">Pokémon</option>
        <option value="Digimon Card Game">Digimon Card Game</option>
        <option value="Flesh and Blood">Flesh and Blood</option>
        <option value="One Piece Card Game">One Piece Card Game</option>
      </select>
    </form>
  );
};

export default SearchBar;
