import React, { useState } from "react";

const SearchBar = ({ items, onSearch, onBrandSelect }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("none");
  const [selectedBrand, setSelectedBrand] = useState("");

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
    onSearch(event.target.value, sortBy, selectedBrand);
  };

  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
    onSearch(searchTerm, event.target.value, selectedBrand);
  };

  const handleBrandSelect = (event) => {
    setSelectedBrand(event.target.value);
    onBrandSelect(event.target.value);
  };

  const getUniqueBrandNames = () => {
    const brandNames = items.map((item) => item.brand);
    const uniqueBrandNames = [...new Set(brandNames)];
    return uniqueBrandNames;
  };

  return (
    <form>
      <label htmlFor="searchTerm">Search:</label>
      <input
        type="text"
        id="searchTerm"
        value={searchTerm}
        onChange={handleSearchTermChange}
      />
      <label htmlFor="sortBy">Sort by:</label>
      <select id="sortBy" value={sortBy} onChange={handleSortByChange}>
        <option value="none">None</option>
        <option value="price">Price (Low to High)</option>
        <option value="price-reverse">Price (High to Low)</option>
        <option value="availability">Availability</option>
      </select>
      <label htmlFor="brand">Brand:</label>
      <select id="brand" value={selectedBrand} onChange={handleBrandSelect}>
        <option value="">All</option>
        {getUniqueBrandNames().map((brandName) => (
          <option key={brandName} value={brandName}>
            {brandName}
          </option>
        ))}
      </select>
    </form>
  );
};

export default SearchBar;
