import React, { useState } from "react";
import Card from "./Card";
import SearchBar from "./SearchBar";
import { FormControlLabel, Checkbox } from "@mui/material";

export default function CardList() {
  const [filteredCards, setFilteredCards] = useState([]);
  const [hideOutOfStock, setHideOutOfStock] = useState(false);
  const [selectedBrand, setSelectedBrand] = useState("");

  const cards = [
    {
      name: "Solemn Judgment - Maze of Memories (MAZE)",
      image: require("./images/card1.jpg"),
      price: 2.24,
      inStock: true,
      brand: "Yu-Gi-Oh!",
    },
    {
      name: "Nibiru, the Primal Being - 2022 Tin of the Pharaoh's Gods (MP22)",
      image: require("./images/card2.jpg"),
      price: 4.01,
      inStock: false,
      brand: "Yu-Gi-Oh!",
    },
    {
      name: "Labyrinth Heavy Tank - Maze of Memories (MAZE)",
      image: require("./images/card3.jpg"),
      price: 0.4,
      inStock: true,
      brand: "Yu-Gi-Oh!",
    },
  ];

  const handleSearch = (searchTerm, sortBy, brand) => {
    let sortedCards = cards;

    if (sortBy === "price") {
      sortedCards = cards.slice().sort((a, b) => a.price - b.price);
    } else if (sortBy === "price-reverse") {
      sortedCards = cards.slice().sort((a, b) => b.price - a.price);
    } else if (sortBy === "availability") {
      sortedCards = cards.slice().sort((a, b) => b.inStock - a.inStock);
    }

    let filteredCards = sortedCards.filter((card) =>
      `${card.name} ${card.price}`
        .toLowerCase()
        .includes(searchTerm.toString().toLowerCase())
    );

    if (brand !== "") {
      filteredCards = filteredCards.filter((card) => card.brand === brand);
    }

    if (hideOutOfStock) {
      filteredCards = filteredCards.filter((card) => card.inStock);
    }

    setSelectedBrand(brand);
    setFilteredCards(filteredCards);
  };

  const renderCards = () => {
    const items = filteredCards.length > 0 ? filteredCards : cards;

    return items.map((card) => (
      <Card
        key={card.name}
        {...card}
        selectedBrand={card.brand === selectedBrand ? selectedBrand : ""}
        style={{ display: "inline-block" }}
      />
    ));
  };

  const handleHideOutOfStockChange = (event) => {
    setHideOutOfStock(event.target.checked);
    if (event.target.checked) {
      setFilteredCards(filteredCards.filter((card) => card.inStock));
    } else {
      handleSearch(searchTerm, sortBy, selectedBrand);
    }
  };

  return (
    <div>
      <SearchBar
        items={cards}
        onSearch={handleSearch}
        onBrandChange={handleSearch}
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={hideOutOfStock}
            onChange={handleHideOutOfStock
}
          />
        }
        label="Hide out of stock"
      />
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
          gap: "20px",
        }}
      >
        {renderCards()}
      </div>
    </div>
  );
}
