import { FormControl, InputLabel, MenuItem, Select } from "@mui/material";
import React, { useState } from "react";
// import { FormControl, InputLabel, MenuItem, Select } from "@material-ui/core";

const SearchBar = ({ items, onSearch }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("none");
  const [brand, setBrand] = useState("");

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
    onSearch(event.target.value, sortBy, brand);
  };

  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
    onSearch(searchTerm, event.target.value, brand);
  };

  const handleBrandChange = (event) => {
    setBrand(event.target.value);
    onSearch(searchTerm, sortBy, event.target.value);
  };

  return (
    <div>
      <label htmlFor="searchTerm">Search:</label>
      <input
        type="text"
        id="searchTerm"
        value={searchTerm}
        onChange={handleSearchTermChange}
      />
      <label htmlFor="sortBy">Sort by:</label>
      <select id="sortBy" value={sortBy} onChange={handleSortByChange}>
        <option value="none">None</option>
        <option value="price">Price (Low to High)</option>
        <option value="price-reverse">Price (High to Low)</option>
        <option value="availability">Availability</option>
      </select>
      <FormControl>
        <InputLabel id="brand-label" style={{ width: 70 }}>Brand</InputLabel>
        <Select
          labelId="brand-label"
          id="brand"
          value={brand}
          onChange={handleBrandChange}
        >
          <MenuItem value="">All Brands</MenuItem>
          <MenuItem value="Magic">Magic</MenuItem>
          <MenuItem value="Yu-Gi-Oh!">Yu-Gi-Oh!</MenuItem>
          <MenuItem value="Pokemon">Pokemon</MenuItem>
          <MenuItem value="Digimon">Digimon</MenuItem>
          <MenuItem value="Flesh and Blood">Flesh and Blood</MenuItem>
          <MenuItem value="One Piece">One Piece</MenuItem>
        </Select>
      </FormControl>
    </div>
  );
};

export default SearchBar;
