import React, { useState } from "react";

const SearchBar = ({ items, onSearch }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("none");
  const [selectedBrand, setSelectedBrand] = useState("All Brands");

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
    onSearch(event.target.value, sortBy, selectedBrand);
  };

  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
    onSearch(searchTerm, event.target.value, selectedBrand);
  };

  const handleBrandChange = (event) => {
    setSelectedBrand(event.target.value);
    onSearch(searchTerm, sortBy, event.target.value);
  };

  // Get unique brand names from the items array
  const brandNames = [...new Set(items.map((item) => item.brand))];

  return (
    <form>
      <label htmlFor="searchTerm">Search:</label>
      <input
        type="text"
        id="searchTerm"
        value={searchTerm}
        onChange={handleSearchTermChange}
      />
      <label htmlFor="sortBy">Sort by:</label>
      <select id="sortBy" value={sortBy} onChange={handleSortByChange}>
        <option value="none">None</option>
        <option value="price">Price (Low to High)</option>
        <option value="price-reverse">Price (High to Low)</option>
        <option value="availability">Availability</option>
      </select>
      <label htmlFor="selectedBrand">Brand:</label>
      <select id="selectedBrand" value={selectedBrand} onChange={handleBrandChange}>
        <option value="All Brands">All Brands</option>
        {brandNames.map((brandName) => (
          <option key={brandName} value={brandName}>
            {brandName}
          </option>
        ))}
      </select>
    </form>
  );
};

export default SearchBar;
